const { assert } = require('chai');
const CancelationService = require('../SWServices/Cancelation/CancelationService');

const { SDKTEST_TOKEN, SDKTEST_USER, SDKTEST_PASSWORD } = process.env;

const SERVICES_URL = 'https://services.test.sw.com.mx';
const UUID = 'e812449c-7364-4b02-9d7f-518abbeec6cb';
const PASSWORD_CSD = '12345678a';
const PASSWORD_PFX = '12345678a';
const RFC = 'EKU9003173C9';
const B64CER = "";
const B64KEY = "";
const B64PFX = "";
const XML = "<Cancelacion xmlns:xsi=\"http://www.w3.org/2001/XMLSchema-instance\" xmlns:xsd=\"http://www.w3.org/2001/XMLSchema\" Fecha=\"2023-07-18T14:08:11\" RfcEmisor=\"EKU9003173C9\" xmlns=\"http://cancelacfd.sat.gob.mx\"><Folios><Folio UUID=\"b6a15ce8-0fb8-401a-bfe7-8930983e182e\" Motivo=\"01\" FolioSustitucion=\"63187375-3433-4ae8-ad5a-3323872026fc\" /></Folios><Folios><Folio UUID=\"63187375-3433-4ae8-ad5a-3323872026fc\" Motivo=\"02\" FolioSustitucion=\"\" /></Folios><Signature xmlns=\"http://www.w3.org/2000/09/xmldsig#\"><SignedInfo><CanonicalizationMethod Algorithm=\"http://www.w3.org/TR/2001/REC-xml-c14n-20010315\" /><SignatureMethod Algorithm=\"http://www.w3.org/2001/04/xmldsig-more#rsa-sha256\" /><Reference URI=\"\"><Transforms><Transform Algorithm=\"http://www.w3.org/2000/09/xmldsig#enveloped-signature\" /></Transforms><DigestMethod Algorithm=\"http://www.w3.org/2001/04/xmlenc#sha256\" /><DigestValue>fFUgI9QsXDrDhrdOzItm0PQKPi75eCBgg5ixjFUmAw0=</DigestValue></Reference></SignedInfo><SignatureValue></SignatureValue><KeyInfo><X509Data><X509IssuerSerial><X509IssuerName>OID.1.2.840.113549.1.9.2=responsable: ACDMA-SAT, OID.2.5.4.45=2.5.4.45, L=COYOACAN, S=CIUDAD DE MEXICO, C=MX, PostalCode=06370, STREET=3ra cerrada de caliz, E=oscar.martinez@sat.gob.mx, OU=SAT-IES Authority, O=SERVICIO DE ADMINISTRACION TRIBUTARIA, CN=AC UAT</X509IssuerName><X509SerialNumber>292233162870206001759766198462772978647764840758</X509SerialNumber></X509IssuerSerial><X509Certificate></X509Certificate></X509Data></KeyInfo></Signature></Cancelacion>";

const EXPECTED_STATUS = 'success';

describe('CancelationService', function () {
	this.timeout(15000);

	const uuidParams = {
		rfc: RFC,
		uuid: UUID,
		motivo: '02',
		folioSustitucion: null
	};

	const csdParams = {
		uuid: UUID,
		passwordCer: PASSWORD_CSD,
		rfc: RFC,
		b64Cer: B64CER,
		b64Key: B64KEY,
		motivo: '02',
		folioSustitucion: null
	};

	const pfxParams = {
		uuid: UUID,
		passwordPfx: PASSWORD_PFX,
		rfc: RFC,
		b64Pfx: B64PFX,
		motivo: '02',
		folioSustitucion: null
	};

	const xmlParams = {
		xml: XML
	};

	describe('CancelationByUUID', () => {
		it('Sucess con autenticación', done => {
			const params = {
				url: SERVICES_URL,
				user: SDKTEST_USER,
				password: SDKTEST_PASSWORD,
				...uuidParams
			};

			CancelationService.Set(params).CancelationByUUID((err, res) => {
				assert.isNull(err, err && err.message);
				assert.equal(res.status, EXPECTED_STATUS);
				done();
			});
		});

		it('Sucess con token', done => {
			const params = {
				url: SERVICES_URL,
				token: SDKTEST_TOKEN,
				...uuidParams
			};

			CancelationService.Set(params).CancelationByUUID((err, res) => {
				assert.isNull(err, err && err.message);
				assert.equal(res.status, EXPECTED_STATUS);
				done();
			});
		});
	});

	describe('CancelationByCSD', () => {
		it('Sucess con autenticación', done => {
			const params = {
				url: SERVICES_URL,
				user: SDKTEST_USER,
				password: SDKTEST_PASSWORD,
				...csdParams
			};

			CancelationService.Set(params).CancelationByCSD((err, res) => {
				assert.isNull(err, err && err.message);
				assert.equal(res.status, EXPECTED_STATUS);
				done();
			});
		});

		it('Sucess con token', done => {
			const params = {
				url: SERVICES_URL,
				token: SDKTEST_TOKEN,
				...csdParams
			};

			CancelationService.Set(params).CancelationByCSD((err, res) => {
				assert.isNull(err, err && err.message);
				assert.equal(res.status, EXPECTED_STATUS);
				done();
			});
		});
	});

	describe('CancelationByPFX', () => {
		it('Sucess con autenticación', done => {
			const params = {
				url: SERVICES_URL,
				user: SDKTEST_USER,
				password: SDKTEST_PASSWORD,
				...pfxParams
			};

			CancelationService.Set(params).CancelationByPFX((err, res) => {
				assert.isNull(err, err && err.message);
				assert.equal(res.status, EXPECTED_STATUS);
				done();
			});
		});

		it('Sucess con token', done => {
			const params = {
				url: SERVICES_URL,
				token: SDKTEST_TOKEN,
				...pfxParams
			};

			CancelationService.Set(params).CancelationByPFX((err, res) => {
				assert.isNull(err, err && err.message);
				assert.equal(res.status, EXPECTED_STATUS);
				done();
			});
		});
	});

	describe('CancelationByXML', () => {
		it('Sucess con autenticación', done => {
			const params = {
				url: SERVICES_URL,
				user: SDKTEST_USER,
				password: SDKTEST_PASSWORD,
				...xmlParams
			};

			CancelationService.Set(params).CancelationByXML((err, res) => {
				assert.isNull(err, err && err.message);
				assert.equal(res.status, EXPECTED_STATUS);
				done();
			});
		});

		it('Sucess con token', done => {
			const params = {
				url: SERVICES_URL,
				token: SDKTEST_TOKEN,
				...xmlParams
			};

			CancelationService.Set(params).CancelationByXML((err, res) => {
				assert.isNull(err, err && err.message);
				assert.equal(res.status, EXPECTED_STATUS);
				done();
			});
		});
	});
});
